namespace SHA_3
{
    class Program
    {
        static string ToBinaryString(string text)
        {
            return string.Join("", Encoding.ASCII.GetBytes(text).Select(n => Convert.ToString(n, 2).PadLeft(8, '0')));
        }
        static void Main(string[] args)
        {
            SHA_3 sha = new SHA_3();
            string message0 = "";
            string message5 = "11001";
            string message1600 = "";
            string message1630 = "";
            Console.WriteLine(sha.GetHashSHA3(message1630, true));
            Console.ReadKey();
        }
    }
}
